import React, { Component } from "react";
import { MDBIcon, MDBCol, MDBCard, MDBCardBody, MDBCardText } from "mdbreact";
import "../Styles/dashboard.css";

class Cards extends Component{
    constructor(props){
        super(props);
        this.state = {
            content: props.content
        }
    }
    render(){
        return(
            <MDBCol sm="4" className="py-3 mt-2 ">
                <MDBCard className="hoverable cardShadow">
                    <MDBCardBody className="pt-2 overflowHidden">
                        <MDBCardText className="pb-3">
                            <span className={`iconHovered p-3 ${this.props.config.color}-gradient`}>
                            <MDBIcon className={`white-text`} size="2x" icon={this.props.config.icon} />
                            </span>
                            <span className="float-right">
                            {this.props.config.header}
                            </span>
                        </MDBCardText>
                        {this.props.content}
                    </MDBCardBody>
                </MDBCard>
            </MDBCol>
        );
    }
}
